import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css"; 

function HomePage() {
  let Links=[
    {name:"HOME",Link:"/"},
    {name:Lo}
  ];
  useEffect(() => {
    const pieces = document.querySelectorAll(".piece");
    setTimeout(() => {
      pieces.forEach((piece) => piece.classList.add("animate"));
    }, 1000); 
  }, []);

  return (
    <div className="bg-purple-100 p-6 min-h-screen flex flex-col">
        <div className=" items-start justify-start inline">
          <img
            src="./logo2.png"
            alt="College Logo"
            className="h-20 transform transition-transform duration-500 ease-in-out hover:scale-110 bright"
          />
        </div>
      {/* <img src="https://media.istockphoto.com/id/1412244027/vector/abstract-purple-watercolor-paint-brush-background.jpg?s=612x612&w=0&k=20&c=vrTzydC1vv9t1CUUzZ7rqJedBdWdWl6zpGs1DUvyYbo="
      className="absolute object-cover h-full w-full overflow-hidden" alt="background"></img> */}
      <nav className="mb-8">
        <ul className="flex space-x-4">
          <li>
            <Link to="/" className="text-purple-500 hover:underline">
              Home
            </Link>
          </li>
          <li>
            <Link to="/login" className="text-purple-500 hover:underline">
              Login
            </Link>
          </li>
          <li>
            <Link to="/register" className="text-purple-500 hover:underline">
              Register
            </Link>
          </li>
        </ul>
      </nav>
      <div className="relative w-full max-w-4xl h-96 overflow-hidden">
        <div className="relative w-full h-full flex flex-wrap">
          {[...Array(10)].map((_, i) => (
            <div
              key={i}
              className={`piece absolute w-3/4 h-full bg-left bg-cover left-0`}
              style={{
                
                backgroundImage: "url(/rec2.jpg)",
                backgroundPosition: `${(i % 5) * 20}% ${Math.floor(i / 5) * 20}%`,
              }}
            ></div>
          ))}
        </div>
      </div>
      <h1 className="text-4xl font-bold mb-4">Welcome to REC</h1>
      <p className="text-2xl text-gray-700">The Place of Knowledge</p>
    </div>
  );
}

export default HomePage;
