import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css"; 

function HomePage() {
  let Links = [
    { name: "HOME", Link: "/" },
    { name: "DASHBOARD", Link: "/dashboard" },
    { name: "LOGIN", Link: "/login" },
    { name: "REGISTER", Link: "/register" }
  ];

  useEffect(() => {
    const pieces = document.querySelectorAll(".piece");
    setTimeout(() => {
      pieces.forEach((piece) => piece.classList.add("animate"));
    }, 1000); 
  }, []);

  return (
    <div className="bg-purple-100 p-6 min-h-screen flex flex-col">
      <div className="flex items-center justify-between mb-8">
        <img
          src="./logo2.png"
          alt="College Logo"
          className="h-20 transform transition-transform duration-500 ease-in-out hover:scale-110 bright"
        />
        <nav>
          <ul className="flex space-x-6">
            {Links.map((link) => (
              <li key={link.name} className="relative text-xl">
                <Link to={link.Link} className="nav-link">{link.name}
                <span className="underline-transition"></span></Link>
              
              </li>
            ))}
          </ul>
        </nav>
      </div>
      <div className="relative w-full max-w-4xl h-96 overflow-hidden">
        <div className="relative w-full h-full flex flex-wrap">
          {[...Array(10)].map((_, i) => (
            <div
              key={i}
              className={`piece absolute w-3/4 h-full bg-left bg-cover left-0 `}
              style={{
                backgroundImage: "url(/rec2.jpg)",
                backgroundPosition: `${(i % 5) * 20}% ${Math.floor(i / 5) * 20}%`,
              }}
            ></div>
          ))}
        </div>
      </div>
      <h1 className="text-4xl font-bold mb-4">Welcome to REC</h1>
      <p className="text-2xl text-gray-700">The Place of Knowledge</p>
    </div>
  );
}

export default HomePage;
