import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css"; 

function HomePage() {
  const Links = [
    { name: "HOME", link: "/" },
    { name: "DASHBOARD", link: "/dashboard" },
    { name: "LOGIN", link: "/login" },
    { name: "REGISTER", link: "/register" }
  ];

  const words = ["Innovate", "Explore", "Learn", "Grow", "Succeed"];
  const [currentWordIndex, setCurrentWordIndex] = useState(0);

  useEffect(() => {
    const pieces = document.querySelectorAll(".piece");
    setTimeout(() => {
      pieces.forEach((piece) => piece.classList.add("animate"));
    }, 1000);

    const intervalId = setInterval(() => {
      setCurrentWordIndex((prevIndex) => (prevIndex + 1) % words.length);
    }, 1000); 

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="bg-purple-100 p-6 min-h-screen flex flex-col">
      <div className="flex items-center justify-between mb-8">
        <img
          src="./logo2.png"
          alt="College Logo"
          className="h-20 transform transition-transform duration-500 ease-in-out hover:scale-110 bright"
        />
        <nav>
          <ul className="flex space-x-6">
            {Links.map((link) => (
              <li key={link.name} className="relative text-xl">
                <Link to={link.link} className="nav-link">
                  {link.name}
                  <span className="underline-transition"></span>
                </Link>
              </li>
            ))}
          </ul>
        </nav>
      </div>
      <div className="relative w-full max-w-4xl h-96 overflow-hidden flex">
        <div className="relative w-3/4 h-full flex flex-wrap">
          {[...Array(10)].map((_, i) => (
            <div
              key={i}
              className={"piece absolute w-full h-full bg-left bg-cover"}
              style={{
                backgroundImage: "url(/rec2.jpg)",
                backgroundPosition: `${(i % 5) * 20}% ${Math.floor(i / 5) * 20}%`,
              }}
            ></div>
          ))}
        </div>
        <div className=" w-1/6 h-full   flex flex-col justify-start items-start p-5 mb-9">
          <h1 className="text-4xl font-bold mb-4  ">Welcome to <span className="text ">
            REC</span></h1>
          <p className="text-2xl text-gray-700 mb-4">The Place of Knowledge</p>
          <p className="text-3xl">{words[currentWordIndex]}</p>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
