import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css"; 

function HomePage() {
  useEffect(() => {
    const pieces = document.querySelectorAll(".piece");
    setTimeout(() => {
      pieces.forEach((piece) => piece.classList.add("animate"));
    }, 1000); // Delay before starting the joining animation
  }, []);

  return (
    <div className="bg-blue-100 p-6 min-h-screen flex flex-col items-center">
      <nav className="mb-8">
        <ul className="flex space-x-4">
          <li>
            <Link to="/" className="text-blue-500 hover:underline">
              Home
            </Link>
          </li>
          <li>
            <Link to="/login" className="text-blue-500 hover:underline">
              Login
            </Link>
          </li>
          <li>
            <Link to="/register" className="text-blue-500 hover:underline">
              Register
            </Link>
          </li>
        </ul>
      </nav>
      <div className="relative w-full max-w-4xl h-96 overflow-hidden">
        <div className="relative w-full h-full flex flex-wrap">
          {[...Array(10)].map((_, i) => (
            <div
              key={i}
              className={`piece absolute w-1/5 h-1/5 bg-center bg-cover`}
              style={{
                backgroundImage: "url(/path-to-your-image/rec2.jpg)",
                backgroundPosition: `${(i % 5) * 20}% ${Math.floor(i / 5) * 20}%`,
              }}
            ></div>
          ))}
        </div>
      </div>
      <h1 className="text-4xl font-bold mb-4">Welcome to REC</h1>
      <p className="text-2xl text-gray-700">The Place of Knowledge</p>
    </div>
  );
}

export default HomePage;
