import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css"; 

function HomePage() {
  const Links = [
    { name: "HOME", link: "/" },
    { name: "DASHBOARD", link: "/dashboard" },
    { name: "LOGIN", link: "/login" },
    { name: "REGISTER", link: "/register" }
  ];

  const words = ["Innovate", "Explore", "Learn", "Grow", "Succeed"];
  const [currentWordIndex, setCurrentWordIndex] = useState(0);

  useEffect(() => {
    const pieces = document.querySelectorAll(".piece");
    setTimeout(() => {
      pieces.forEach((piece) => piece.classList.add("animate"));
    }, 1000);

    const intervalId = setInterval(() => {
      setCurrentWordIndex((prevIndex) => (prevIndex + 1) % words.length);
    }, 1000); 

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="bg-purple-100 p-6 min-h-screen flex flex-col">
      <div className="flex items-center justify-between mb-8">
        <img
          src="./logo2.png"
          alt="College Logo"
          className="h-20 transform transition-transform duration-500 ease-in-out hover:scale-110 bright"
        />
        <nav>
          <ul className="flex space-x-6">
            {Links.map((link) => (
              <li key={link.name} className="relative text-xl">
                <Link to={link.link} className="nav-link">
                  {link.name}
                  <span className="underline-transition"></span>
                </Link>
              </li>
            ))}
          </ul>
        </nav>
      </div>
      <div className="relative max-w-7xl  h-96 overflow-hidden flex">
        <div className="relative w-1/2 h-full flex flex-wrap">
          {[...Array(10)].map((_, i) => (
            <div
              key={i}
              className={"piece absolute w-full h-full bg-left bg-cover"}
              style={{
                backgroundImage: "url(/rec2.jpg)",
                backgroundPosition: `${(i % 5) * 20}% ${Math.floor(i / 5) * 20}%`,
              }}
            ></div>
          ))}
        </div>
        <div className="  h-full leading-normal flex flex-col justify-start place-items-center p-8 mb-9">
          <h1 className="text-5xl font-bold mb-4 text-purple-500 leading-normal">Welcome To <span className="text-6xl text-purple-600">
            REC</span></h1>
          <p className="text-2xl text-gray-700 mb-4">The Place of Knowledge</p>
          <p className="text-3xl text-purple-800">{words[currentWordIndex]}</p>
        </div>
      </div>
      <div>
          <p className="text-1xl space-x-2">Rajalakshmi Engineering College, an autonomous institution affiliated to Anna University, Chennai, was established in the year 1997 under the aegis of Rajalakshmi Educational Trust whose members have had consummate experience in the fields of education and industry. The College has grown from strength to strength in the last 25 years and progressing towards Excellence in Engineering Education, Research and Development.</p>
        </div>
        <div className="relative max-w-xs overflow-hidden rounded-2xl shadow-lg group">
          <img src="url(/das.png)" alt="dashborad" className="transition" />
        </div>
    </div>
  );
}

export default HomePage;
